rules.expr:
kube_pod_labels * on (namespace, pod) group_right(label_alertmanager_i_qed_ai_project) (
      increase(kube_pod_container_status_restarts_total[30m]) > 0
    and on (namespace, pod, container)
      kube_pod_container_status_last_terminated_reason{reason="OOMKilled"}
  # exclude specific instances that are not worth addressing
  unless on (namespace, pod, container)
    (
        # data portals
        kube_pod_container_status_restarts_total{container="object-file-browser"}
      or
        # smokeping_prober slowly leaks memory and gets killed once every few days
        # As of 2025-07-29 it's deployed only on gke-{test,prod}, but the plan is to
        # ultimately migrate it to other clusters.
        kube_pod_container_status_restarts_total{
          container="smokeping-prober",
          pod=~"smokeping-prober-.+",
        }
    )
)

